import json
from pathlib import Path
import numpy as np
from sentence_transformers import SentenceTransformer
ROOT = Path(__file__).parent
# Files
PHOENIX = ROOT / "phoenix_nano_memory.jsonl"
PERSONALITY_MAIN = ROOT / "personality_vectors_local_original.jsonl"
PERSONALITY_SECONDARY = ROOT / "personality_vectors_local.jsonl"
# Load embedding model
model = SentenceTransformer("all-MiniLM-L6-v2", device="cpu")
def load_jsonl(path):
    if not path.exists():
        return []
    return [json.loads(line) for line in path.read_text().splitlines() if line.strip()]
def build_index():
    entries = []
    vectors = []
    for path in [PHOENIX, PERSONALITY_MAIN, PERSONALITY_SECONDARY]:
        if path.exists():
            for entry in load_jsonl(path):
                if "text" in entry:
                    text = entry["text"]
                elif "content" in entry:
                    text = entry["content"]
                else:
                    text = str(entry)

                entries.append(entry)
                vectors.append(model.encode(text))

    return entries, np.array(vectors)
# Global index
ENTRIES, VECTORS = build_index()
def embed_query_text(text):
    return model.encode(text)
def search_memory(query_vec, top_k=5):
    sims = np.dot(VECTORS, query_vec) / (
        np.linalg.norm(VECTORS, axis=1) * np.linalg.norm(query_vec)
    )
    idxs = sims.argsort()[::-1][:top_k]
    return [(ENTRIES[i], float(sims[i])) for i in idxs]

