const int coilPin = 9;

void setup() {
  pinMode(coilPin, OUTPUT);
}

void loop() {
  // 528 Hz = period of ~1894 microseconds
  // Half period = 947 microseconds
  digitalWrite(coilPin, HIGH);
  delayMicroseconds(947);
  digitalWrite(coilPin, LOW);
  delayMicroseconds(947);
}
